loginUrl:
//获取用户输入的账号密码
userInfo=source.getLoginInfoMap()
java.log(userInfo)
UNE=userInfo['账号']
PWD=userInfo['密码']

if(PWD&&UNE){
//模拟登录
    java.log('尝试登录中...')
//登录请求构造
    body='username='+UNE+'&password='+PWD
    url='http://www.qinxiaoshuo.com/api/user/login,'
    options={
        'method': 'POST',
        'body': String(body)
    }
    url=url+JSON.stringify(options)
    data=JSON.parse(java.ajax(url))
    headers={'Cookie':'token='+data.Token}
    headers=JSON.stringify(headers)
    source.putLoginHeader(headers)
}

