ruleToc.chapterList:
$.data.list
@js:
eval(String(source.bookSourceComment));
java["toast"]("\nurl：" + url + "\nuri：" + uri);
id = java.get("id");
cid = Math.floor(id / 1e3 + 1);
//二级目录
//章节名显示卷名(true/false)
var opts = {
  showVolName: false
};

//净化章节名
function format(title) {
  return title
    .trim()
    .replace(/^(\d+)(?![\s\d卷部章节回.])/, "$1 ")
    .replace(/\s+/g, "\x20");
}

var list = [];
JSON.parse(result).forEach((vol) => {
  var chs = vol.list.map((ch) => ({
    text: !vol.name
      ? format(ch.name)
      : (opts.showVolName ? format(vol.name) + "☪ " : "").padStart(3, "") +
        format(ch.name)
          .replace(/^(正文|VIP章节|最新章节)?(\s+|_)|[\(（【].*?[求更谢乐发订合贺补加推票章字修防kK].*?[】）\)]*.*/g,"")
          .replace(/.*[弟地滴](.*)[篇张樟]/, "第$1章"),
    href:uri + "/BookFiles/Html/" + cid + "/" + id + "/" + ch.id + ".html"
  }));

  if (vol.name != "") {
    list.push({
      text: "📖[" + format(vol.name) + "]📖",
      isVolume: true
    });
  }
  list = list.concat(chs);
});
//java.log(JSON.stringify(list))
list;

