bookSourceComment:
var time = (new Date().getTime() / 1000).toFixed(0);
function getUrl(url, method, data) {
  eval(
    String(
      java.importScript(
        "https://cdnjs.cloudflare.com/ajax/libs/crypto-js/3.1.2/rollups/hmac-sha256.js"
      )
    )
  );
  const picacg = {
    BaseUrl: "https://picaapi.picacomic.com/",
    apiKey: "C69BAF41DA5ABD1FFEDC6D2FEA56B",
    nonce: "65991e3d30f141a6b9bbc475b22b1d17",
    secretKey:
      "~d}$Q7$eIni=V)9\\RK/P.RM4;9[7|@/CA}b~OW!3?EV`:<>M7pddUBL5n|0/*Cn",
    headers: {
      accept: "application/vnd.picacomic.com.v1+json",
      "app-channel": "1",
      time: time,
      "app-version": "2.2.1.2.3.4",
      "app-uuid": "defaultUuid",
      "app-platform": "android",
      "app-build-version": "45",
      "Content-Type": "application/json; charset=UTF-8",
      "User-Agent": "okhttp/3.8.1",
      "image-quality": "original"
    }
  };
  const request_url = picacg.BaseUrl + url;
  let headers = picacg.headers;
  headers["api-key"] = picacg.apiKey;
  headers.nonce = picacg.nonce;
  let raw =
    url.replace(picacg.BaseUrl, "") +
    headers.time +
    picacg.nonce +
    method +
    picacg.apiKey;
  raw = raw.toLocaleLowerCase();
  headers.signature = CryptoJS.HmacSHA256(raw, picacg.secretKey).toString(
    CryptoJS.enc.Hex
  );
  if (source.getLoginHeaderMap()) {
    headers.authorization = source.getLoginHeaderMap().authorization;
  }
  option = JSON.stringify({ method: method, body: data, headers: headers });
  return request_url + "," + option;
}

