    return login(`【源站启用提示】${n(3)}${k(3)}启用失败，没有选中源站`);
  } else {
    delete $$$.i;
    if (i == $$$.ci0) {
      login(`【源站启用提示】${n(3)}${k(3)}正在使用此源站，无需启用`);
      return put($$$);
