    login(`【源站启用提示】${n(3)}${k(8)}启用成功，已选择源站${l(8)}` +
      "\n     " + name(i));
    $$$.url = $$[i][1];
    $$$.ci3 = $$$.ci2
    $$$.ci2 = $$$.ci1
    $$$.ci1 = $$$.ci0
    $$$.ci0 = i;
    return put($$$);
  }
}

