eval(String(source.bookSourceComment));
var list = java.getElements(".chapter_list a"),
  data = [];
date = String(java.getElements(".anime__details__widget").text()).replace(/最近更新：|\s/g, "");
for (i in list) {
  title = list[i].text();
  order = title.match(/(\d+)/);
  title = settle == "true"
    ? String(title)
      .replace(/\s-\s|-/g, " ").replace(/\s+/, " ")
      .replace(/.*第(\d+)[话話章](|\s)第.*?[话話章]/, "$1")
      .replace(/.*第(\d+)[话話章][^\s]/, "$1 ")
      .replace(/.*第(\d+)[话話章]\s\d+/, "$1")
      .replace(/.*第.*(\d+).*?[话話章]/, "$1")
      .replace(/(\d+)/, title.match(/\d+/) ? order[1].padStart(3, '0') : "$1")
    : title;
  data.push({
    title:
      i == 0 ? `${title}〔${date}〕` : title,
    link: list[i].attr("href")+`,{"Referer":${baseUrl}}`
  });
};
JSON.stringify(data);
</js>
