-<js>
eval(String(source.bookSourceComment));
var list = java.getElements("#ul2 .w-full a"),
  data = [];
date = java.getElements(".text-sm.text-gray-800.4").text();
for (i in list) {
  title = list[i].text()
  title = String(title)
    .replace(/\s-\s|-/g, " ").replace(/\s+/, " ")
    .replace(/.*第(\d+)[话話章](|\s)第.*?[话話章]/, "$1")
    .replace(/.*第(\d+)[话話章][^\s]/, "$1 ")
    .replace(/.*第(\d+)[话話章]\s\d+/, "$1")
    .replace(/.*第.*(\d+).*?[话話章]/, "$1")
    .replace(/(\d+)/, title.match(/\d+/) ? title.match(/(\d+)/)[1].padStart(3, '0') : "$1");
  data.push({
    title:
      i == 0 ? `${title}〔${date}〕` : title,
    link: list[i].attr("href")
  });
};
JSON.stringify(data);
</js>
