  if (TC_to_SC == undefined) TC_to_SC = "true";
  if ($$$.TC_to_SC == "true") {
    login(`【简繁转换提示】${n(3)}${k(2)}已关闭繁体转换，请刷新书籍`);
    $$$.TC_to_SC = "false";
    return put($$$);
  } else {
    login(`【简繁转换提示】${n(3)}${k(2)}已开启繁体转换，请刷新书籍`);
    $$$.TC_to_SC = "true";
    return put($$$);
  }
}


//目录整理开关
function settle() {
  if (settle == undefined) settle = "true";
  if ($$$.settle == "true") {
    login(`【目录整理提示】${n(3)}${k(2)}已关闭目录整理，请刷新目录`);
    $$$.settle = "false";
    return put($$$);
  } else {
    login(`【目录整理提示】${n(3)}${k(2)}已开启目录整理，请刷新目录`);
    $$$.settle = "true";
    return put($$$);
  }
}


//确定启用源站
function yes() {
  i = $$$.i;
  if (i == undefined) {
    return login(`【源站启用提示】${n(3)}${k(3)}启用失败，没有选中源站`);
  } else {
    delete $$$.i;
    if (i == $$$.ci0) {
      login(`【源站启用提示】${n(3)}${k(3)}正在使用此源站，无需启用`);
      return put($$$);
    } 
    login(`【源站启用提示】${n(3)}${k(8)}启用成功，已选择源站${l(8)}` +
      "\n     " + name(i));
    $$$.url = $$[i][1];
    $$$.ci3 = $$$.ci2
    $$$.ci2 = $$$.ci1
    $$$.ci1 = $$$.ci0
    $$$.ci0 = i;
    return put($$$);
  }
}

